function Itemes_insert(&$error_message = '') {
	global $Translation;

	// mm: can member insert record?
	$arrPerm = getTablePermissions('Itemes');
	if(!$arrPerm['insert']) return false;

	$data = [
		'Cod_item' => Request::lookup('Cod_item', ''),
		'Estado_Conservacion' => Request::lookup('Estado_Conservacion', ''),
		'Num_Copia' => Request::val('Num_Copia', ''),
		'Cod_Ejemplar' => Request::val('Cod_Ejemplar', ''),
		'Estado_de_Baja' => Request::val('Estado_de_Baja', ''),
	];


	// hook: Itemes_before_insert
	if(function_exists('Itemes_before_insert')) {
		$args = [];
		if(!Itemes_before_insert($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$error = '';
	// set empty fields to NULL
	$data = array_map(function($v) { return ($v === '' ? NULL : $v); }, $data);
	insert('Itemes', backtick_keys_once($data), $error);
	if($error) {
		$error_message = $error;
		return false;
	}

	$recID = db_insert_id(db_link());

	update_calc_fields('Itemes', $recID, calculated_fields()['Itemes']);

	// hook: Itemes_after_insert
	if(function_exists('Itemes_after_insert')) {
		$res = sql("SELECT * FROM `Itemes` WHERE `id`='" . makeSafe($recID, false) . "' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) {
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = makeSafe($recID, false);
		$args = [];
		if(!Itemes_after_insert($data, getMemberInfo(), $args)) { return $recID; }
	}

	// mm: save ownership data
	set_record_owner('Itemes', $recID, getLoggedMemberID());

	// if this record is a copy of another record, copy children if applicable
	if(strlen(Request::val('SelectedID'))) Itemes_copy_children($recID, Request::val('SelectedID'));

	return $recID;
}

