	if($AllowInsert) {
		if(!$selected_id) $templateCode = str_replace('<%%INSERT_BUTTON%%>', '<button type="submit" class="btn btn-success" id="insert" name="insert_x" value="1" onclick="return Itemes_validateData();"><i class="glyphicon glyphicon-plus-sign"></i> ' . $Translation['Save New'] . '</button>', $templateCode);
		$templateCode = str_replace('<%%INSERT_BUTTON%%>', '<button type="submit" class="btn btn-default" id="insert" name="insert_x" value="1" onclick="return Itemes_validateData();"><i class="glyphicon glyphicon-plus-sign"></i> ' . $Translation['Save As Copy'] . '</button>', $templateCode);
	} else {
		$templateCode = str_replace('<%%INSERT_BUTTON%%>', '', $templateCode);
	}

	// 'Back' button action
	if(Request::val('Embedded')) {
		$backAction = 'AppGini.closeParentModal(); return false;';
	} else {
		$backAction = '$j(\'form\').eq(0).attr(\'novalidate\', \'novalidate\'); document.myform.reset(); return true;';
	}

	if($selected_id) {
		if(!Request::val('Embedded')) $templateCode = str_replace('<%%DVPRINT_BUTTON%%>', '<button type="submit" class="btn btn-default" id="dvprint" name="dvprint_x" value="1" onclick="$j(\'form\').eq(0).prop(\'novalidate\', true); document.myform.reset(); return true;" title="' . html_attr($Translation['Print Preview']) . '"><i class="glyphicon glyphicon-print"></i> ' . $Translation['Print Preview'] . '</button>', $templateCode);
		if($AllowUpdate) {
			$templateCode = str_replace('<%%UPDATE_BUTTON%%>', '<button type="submit" class="btn btn-success btn-lg" id="update" name="update_x" value="1" onclick="return Itemes_validateData();" title="' . html_attr($Translation['Save Changes']) . '"><i class="glyphicon glyphicon-ok"></i> ' . $Translation['Save Changes'] . '</button>', $templateCode);
		} else {
			$templateCode = str_replace('<%%UPDATE_BUTTON%%>', '', $templateCode);
		}
		if(($arrPerm['delete'] == 1 && $ownerMemberID == getLoggedMemberID()) || ($arrPerm['delete'] == 2 && $ownerGroupID == getLoggedGroupID()) || $arrPerm['delete'] == 3) { // allow delete?
			$templateCode = str_replace('<%%DELETE_BUTTON%%>', '<button type="submit" class="btn btn-danger" id="delete" name="delete_x" value="1" title="' . html_attr($Translation['Delete']) . '"><i class="glyphicon glyphicon-trash"></i> ' . $Translation['Delete'] . '</button>', $templateCode);
		} else {
			$templateCode = str_replace('<%%DELETE_BUTTON%%>', '', $templateCode);
		}
		$templateCode = str_replace('<%%DESELECT_BUTTON%%>', '<button type="submit" class="btn btn-default" id="deselect" name="deselect_x" value="1" onclick="' . $backAction . '" title="' . html_attr($Translation['Back']) . '"><i class="glyphicon glyphicon-chevron-left"></i> ' . $Translation['Back'] . '</button>', $templateCode);
	} else {
		$templateCode = str_replace('<%%UPDATE_BUTTON%%>', '', $templateCode);
		$templateCode = str_replace('<%%DELETE_BUTTON%%>', '', $templateCode);

		// if not in embedded mode and user has insert only but no view/update/delete,
		// remove 'back' button
		if(
			$arrPerm['insert']
			&& !$arrPerm['update'] && !$arrPerm['delete'] && !$arrPerm['view']
			&& !Request::val('Embedded')
		)
			$templateCode = str_replace('<%%DESELECT_BUTTON%%>', '', $templateCode);
		elseif($separateDV)
			$templateCode = str_replace(
				'<%%DESELECT_BUTTON%%>', 
				'<button
					type="submit" 
					class="btn btn-default" 
					id="deselect" 
					name="deselect_x" 
					value="1" 
					onclick="' . $backAction . '" 
					title="' . html_attr($Translation['Back']) . '">
						<i class="glyphicon glyphicon-chevron-left"></i> ' .
						$Translation['Back'] .
				'</button>',
				$templateCode
			);
		else
			$templateCode = str_replace('<%%DESELECT_BUTTON%%>', '', $templateCode);
	}

	// set records to read only if user can't insert new records and can't edit current record
	if(($selected_id && !$AllowUpdate && !$AllowInsert) || (!$selected_id && !$AllowInsert)) {
		$jsReadOnly = '';
		$jsReadOnly .= "\tjQuery('#Cod_item').prop('disabled', true).css({ color: '#555', backgroundColor: '#fff' });\n";
		$jsReadOnly .= "\tjQuery('#Cod_item_caption').prop('disabled', true).css({ color: '#555', backgroundColor: 'white' });\n";
		$jsReadOnly .= "\tjQuery('#Estado_Conservacion').prop('disabled', true).css({ color: '#555', backgroundColor: '#fff' });\n";
		$jsReadOnly .= "\tjQuery('#Estado_Conservacion_caption').prop('disabled', true).css({ color: '#555', backgroundColor: 'white' });\n";
		$jsReadOnly .= "\tjQuery('#Num_Copia').replaceWith('<div class=\"form-control-static\" id=\"Num_Copia\">' + (jQuery('#Num_Copia').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#Cod_Ejemplar').replaceWith('<div class=\"form-control-static\" id=\"Cod_Ejemplar\">' + (jQuery('#Cod_Ejemplar').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#Estado_de_Baja').replaceWith('<div class=\"form-control-static\" id=\"Estado_de_Baja\">' + (jQuery('#Estado_de_Baja').val() || '') + '</div>'); jQuery('#Estado_de_Baja-multi-selection-help').hide();\n";
		$jsReadOnly .= "\tjQuery('.select2-container').hide();\n";

		$noUploads = true;
	} elseif($AllowInsert) {
		$jsEditable = "\tjQuery('form').eq(0).data('already_changed', true);"; // temporarily disable form change handler
		$jsEditable .= "\tjQuery('form').eq(0).data('already_changed', false);"; // re-enable form change handler
	}

	// process combos
	$templateCode = str_replace('<%%COMBO(Cod_item)%%>', $combo_Cod_item->HTML, $templateCode);
	$templateCode = str_replace('<%%COMBOTEXT(Cod_item)%%>', $combo_Cod_item->MatchText, $templateCode);
	$templateCode = str_replace('<%%URLCOMBOTEXT(Cod_item)%%>', urlencode($combo_Cod_item->MatchText), $templateCode);
	$templateCode = str_replace('<%%COMBO(Estado_Conservacion)%%>', $combo_Estado_Conservacion->HTML, $templateCode);
	$templateCode = str_replace('<%%COMBOTEXT(Estado_Conservacion)%%>', $combo_Estado_Conservacion->MatchText, $templateCode);
	$templateCode = str_replace('<%%URLCOMBOTEXT(Estado_Conservacion)%%>', urlencode($combo_Estado_Conservacion->MatchText), $templateCode);
	$templateCode = str_replace('<%%COMBO(Estado_de_Baja)%%>', $combo_Estado_de_Baja->HTML, $templateCode);
	$templateCode = str_replace('<%%COMBOTEXT(Estado_de_Baja)%%>', $combo_Estado_de_Baja->SelectedData, $templateCode);

	/* lookup fields array: 'lookup field name' => ['parent table name', 'lookup field caption'] */
	$lookup_fields = ['Cod_item' => ['Catalogo', 'Cod item'], 'Estado_Conservacion' => ['Estado_Conservacion', 'Estado Conservacion'], ];
	foreach($lookup_fields as $luf => $ptfc) {
		$pt_perm = getTablePermissions($ptfc[0]);

		// process foreign key links
		if($pt_perm['view'] || $pt_perm['edit']) {
			$templateCode = str_replace("<%%PLINK({$luf})%%>", '<button type="button" class="btn btn-default view_parent" id="' . $ptfc[0] . '_view_parent" title="' . html_attr($Translation['View'] . ' ' . $ptfc[1]) . '"><i class="glyphicon glyphicon-eye-open"></i></button>', $templateCode);
		}

		// if user has insert permission to parent table of a lookup field, put an add new button
		if($pt_perm['insert'] /* && !Request::val('Embedded')*/) {
			$templateCode = str_replace("<%%ADDNEW({$ptfc[0]})%%>", '<button type="button" class="btn btn-default add_new_parent" id="' . $ptfc[0] . '_add_new" title="' . html_attr($Translation['Add New'] . ' ' . $ptfc[1]) . '"><i class="glyphicon glyphicon-plus text-success"></i></button>', $templateCode);
		}
	}

	// process images
	$templateCode = str_replace('<%%UPLOADFILE(id)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(Cod_item)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(Estado_Conservacion)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(Num_Copia)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(Cod_Ejemplar)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(Estado_de_Baja)%%>', '', $templateCode);

	// process values
	if($selected_id) {
		if( $dvprint) $templateCode = str_replace('<%%VALUE(id)%%>', safe_html($urow['id']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(id)%%>', html_attr($row['id']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(id)%%>', urlencode($urow['id']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(Cod_item)%%>', safe_html($urow['Cod_item']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(Cod_item)%%>', html_attr($row['Cod_item']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Cod_item)%%>', urlencode($urow['Cod_item']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(Estado_Conservacion)%%>', safe_html($urow['Estado_Conservacion']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(Estado_Conservacion)%%>', html_attr($row['Estado_Conservacion']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Estado_Conservacion)%%>', urlencode($urow['Estado_Conservacion']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(Num_Copia)%%>', safe_html($urow['Num_Copia']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(Num_Copia)%%>', html_attr($row['Num_Copia']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Num_Copia)%%>', urlencode($urow['Num_Copia']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(Cod_Ejemplar)%%>', safe_html($urow['Cod_Ejemplar']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(Cod_Ejemplar)%%>', html_attr($row['Cod_Ejemplar']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Cod_Ejemplar)%%>', urlencode($urow['Cod_Ejemplar']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(Estado_de_Baja)%%>', safe_html($urow['Estado_de_Baja']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(Estado_de_Baja)%%>', html_attr($row['Estado_de_Baja']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Estado_de_Baja)%%>', urlencode($urow['Estado_de_Baja']), $templateCode);
	} else {
		$templateCode = str_replace('<%%VALUE(id)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(id)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(Cod_item)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Cod_item)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(Estado_Conservacion)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Estado_Conservacion)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(Num_Copia)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Num_Copia)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(Cod_Ejemplar)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Cod_Ejemplar)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(Estado_de_Baja)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Estado_de_Baja)%%>', urlencode(''), $templateCode);
	}

	// process translations
	$templateCode = parseTemplate($templateCode);

	// clear scrap
	$templateCode = str_replace('<%%', '<!-- ', $templateCode);
	$templateCode = str_replace('%%>', ' -->', $templateCode);

	// hide links to inaccessible tables
	if(Request::val('dvprint_x') == '') {
		$templateCode .= "\n\n<script>\$j(function() {\n";
		$arrTables = getTableList();
		foreach($arrTables as $name => $caption) {
			$templateCode .= "\t\$j('#{$name}_link').removeClass('hidden');\n";
			$templateCode .= "\t\$j('#xs_{$name}_link').removeClass('hidden');\n";
		}

		$templateCode .= $jsReadOnly;
		$templateCode .= $jsEditable;

		if(!$selected_id) {
		}

		$templateCode.="\n});</script>\n";
	}

	// ajaxed auto-fill fields
	$templateCode .= '<script>';
	$templateCode .= '$j(function() {';


	$templateCode.="});";
	$templateCode.="</script>";
	$templateCode .= $lookups;

	// handle enforced parent values for read-only lookup fields
	$filterField = Request::val('FilterField');
	$filterOperator = Request::val('FilterOperator');
	$filterValue = Request::val('FilterValue');

	// don't include blank images in lightbox gallery
	$templateCode = preg_replace('/blank.gif" data-lightbox=".*?"/', 'blank.gif"', $templateCode);

	// don't display empty email links
	$templateCode=preg_replace('/<a .*?href="mailto:".*?<\/a>/', '', $templateCode);

	/* default field values */
	$rdata = $jdata = get_defaults('Itemes');
	if($selected_id) {
		$jdata = get_joined_record('Itemes', $selected_id);
		if($jdata === false) $jdata = get_defaults('Itemes');
		$rdata = $row;
	}
	$templateCode .= loadView('Itemes-ajax-cache', ['rdata' => $rdata, 'jdata' => $jdata]);

	// hook: Itemes_dv
	if(function_exists('Itemes_dv')) {
		$args = [];
		Itemes_dv(($selected_id ? $selected_id : FALSE), getMemberInfo(), $templateCode, $args);
	}

	return $templateCode;
}