	if(!$arrPerm['insert']) return false;

	$data = [
		'Codigo_de_Registro' => Request::val('Codigo_de_Registro', ''),
		'Codigo_Barras' => Request::val('Codigo_Barras', ''),
		'id_Estado_Conservacion' => Request::lookup('id_Estado_Conservacion', ''),
		'id_Estado_Prestable' => Request::lookup('id_Estado_Prestable', ''),
		'Fecha_Regsitro' => Request::dateComponents('Fecha_Regsitro', ''),
	];


	// hook: Registros_before_insert
	if(function_exists('Registros_before_insert')) {
		$args = [];
		if(!Registros_before_insert($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$error = '';
	// set empty fields to NULL
	$data = array_map(function($v) { return ($v === '' ? NULL : $v); }, $data);
	insert('Registros', backtick_keys_once($data), $error);
	if($error) {
		$error_message = $error;
		return false;
	}

	$recID = db_insert_id(db_link());

	update_calc_fields('Registros', $recID, calculated_fields()['Registros']);

	// hook: Registros_after_insert
	if(function_exists('Registros_after_insert')) {
		$res = sql("SELECT * FROM `Registros` WHERE `id`='" . makeSafe($recID, false) . "' LIMIT 1", $eo);
		if($row = db_fetch_assoc($res)) {
			$data = array_map('makeSafe', $row);
		}
		$data['selectedID'] = makeSafe($recID, false);
		$args = [];
		if(!Registros_after_insert($data, getMemberInfo(), $args)) { return $recID; }
	}

	// mm: save ownership data
	set_record_owner('Registros', $recID, getLoggedMemberID());

	// if this record is a copy of another record, copy children if applicable
	if(strlen(Request::val('SelectedID'))) Registros_copy_children($recID, Request::val('SelectedID'));

	return $recID;
}

