		$templateCode = @file_get_contents($template_file);
	}

	// process form title
	$templateCode = str_replace('<%%DETAIL_VIEW_TITLE%%>', 'Registro details', $templateCode);
	$templateCode = str_replace('<%%RND1%%>', $rnd1, $templateCode);
	$templateCode = str_replace('<%%EMBEDDED%%>', (Request::val('Embedded') ? 'Embedded=1' : ''), $templateCode);
	// process buttons
	if($AllowInsert) {
		if(!$selected_id) $templateCode = str_replace('<%%INSERT_BUTTON%%>', '<button type="submit" class="btn btn-success" id="insert" name="insert_x" value="1" onclick="return Registros_validateData();"><i class="glyphicon glyphicon-plus-sign"></i> ' . $Translation['Save New'] . '</button>', $templateCode);
		$templateCode = str_replace('<%%INSERT_BUTTON%%>', '<button type="submit" class="btn btn-default" id="insert" name="insert_x" value="1" onclick="return Registros_validateData();"><i class="glyphicon glyphicon-plus-sign"></i> ' . $Translation['Save As Copy'] . '</button>', $templateCode);
	} else {
		$templateCode = str_replace('<%%INSERT_BUTTON%%>', '', $templateCode);
	}

	// 'Back' button action
	if(Request::val('Embedded')) {
		$backAction = 'AppGini.closeParentModal(); return false;';
	} else {
		$backAction = '$j(\'form\').eq(0).attr(\'novalidate\', \'novalidate\'); document.myform.reset(); return true;';
	}

	if($selected_id) {
		if(!Request::val('Embedded')) $templateCode = str_replace('<%%DVPRINT_BUTTON%%>', '<button type="submit" class="btn btn-default" id="dvprint" name="dvprint_x" value="1" onclick="$j(\'form\').eq(0).prop(\'novalidate\', true); document.myform.reset(); return true;" title="' . html_attr($Translation['Print Preview']) . '"><i class="glyphicon glyphicon-print"></i> ' . $Translation['Print Preview'] . '</button>', $templateCode);
		if($AllowUpdate) {
			$templateCode = str_replace('<%%UPDATE_BUTTON%%>', '<button type="submit" class="btn btn-success btn-lg" id="update" name="update_x" value="1" onclick="return Registros_validateData();" title="' . html_attr($Translation['Save Changes']) . '"><i class="glyphicon glyphicon-ok"></i> ' . $Translation['Save Changes'] . '</button>', $templateCode);
		} else {
			$templateCode = str_replace('<%%UPDATE_BUTTON%%>', '', $templateCode);
		}
		if(($arrPerm['delete'] == 1 && $ownerMemberID == getLoggedMemberID()) || ($arrPerm['delete'] == 2 && $ownerGroupID == getLoggedGroupID()) || $arrPerm['delete'] == 3) { // allow delete?
			$templateCode = str_replace('<%%DELETE_BUTTON%%>', '<button type="submit" class="btn btn-danger" id="delete" name="delete_x" value="1" title="' . html_attr($Translation['Delete']) . '"><i class="glyphicon glyphicon-trash"></i> ' . $Translation['Delete'] . '</button>', $templateCode);
		} else {
			$templateCode = str_replace('<%%DELETE_BUTTON%%>', '', $templateCode);
		}
		$templateCode = str_replace('<%%DESELECT_BUTTON%%>', '<button type="submit" class="btn btn-default" id="deselect" name="deselect_x" value="1" onclick="' . $backAction . '" title="' . html_attr($Translation['Back']) . '"><i class="glyphicon glyphicon-chevron-left"></i> ' . $Translation['Back'] . '</button>', $templateCode);
	} else {
		$templateCode = str_replace('<%%UPDATE_BUTTON%%>', '', $templateCode);
		$templateCode = str_replace('<%%DELETE_BUTTON%%>', '', $templateCode);

		// if not in embedded mode and user has insert only but no view/update/delete,
		// remove 'back' button
		if(
			$arrPerm['insert']
			&& !$arrPerm['update'] && !$arrPerm['delete'] && !$arrPerm['view']
			&& !Request::val('Embedded')
		)
			$templateCode = str_replace('<%%DESELECT_BUTTON%%>', '', $templateCode);
		elseif($separateDV)
			$templateCode = str_replace(
				'<%%DESELECT_BUTTON%%>', 
				'<button
					type="submit" 
					class="btn btn-default" 
					id="deselect" 
					name="deselect_x" 
					value="1" 
					onclick="' . $backAction . '" 
					title="' . html_attr($Translation['Back']) . '">
						<i class="glyphicon glyphicon-chevron-left"></i> ' .
						$Translation['Back'] .
				'</button>',
				$templateCode
			);
		else
			$templateCode = str_replace('<%%DESELECT_BUTTON%%>', '', $templateCode);
	}

	// set records to read only if user can't insert new records and can't edit current record
	if(($selected_id && !$AllowUpdate && !$AllowInsert) || (!$selected_id && !$AllowInsert)) {
		$jsReadOnly = '';
		$jsReadOnly .= "\tjQuery('#Codigo_de_Registro').replaceWith('<div class=\"form-control-static\" id=\"Codigo_de_Registro\">' + (jQuery('#Codigo_de_Registro').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#Codigo_Barras').replaceWith('<div class=\"form-control-static\" id=\"Codigo_Barras\">' + (jQuery('#Codigo_Barras').val() || '') + '</div>');\n";
		$jsReadOnly .= "\tjQuery('#id_Estado_Conservacion').prop('disabled', true).css({ color: '#555', backgroundColor: '#fff' });\n";
		$jsReadOnly .= "\tjQuery('#id_Estado_Conservacion_caption').prop('disabled', true).css({ color: '#555', backgroundColor: 'white' });\n";
		$jsReadOnly .= "\tjQuery('#id_Estado_Prestable').prop('disabled', true).css({ color: '#555', backgroundColor: '#fff' });\n";
		$jsReadOnly .= "\tjQuery('#id_Estado_Prestable_caption').prop('disabled', true).css({ color: '#555', backgroundColor: 'white' });\n";
		$jsReadOnly .= "\tjQuery('#Fecha_Regsitro').prop('readonly', true);\n";
		$jsReadOnly .= "\tjQuery('#Fecha_RegsitroDay, #Fecha_RegsitroMonth, #Fecha_RegsitroYear').prop('disabled', true).css({ color: '#555', backgroundColor: '#fff' });\n";
		$jsReadOnly .= "\tjQuery('.select2-container').hide();\n";

		$noUploads = true;
	} elseif($AllowInsert) {
		$jsEditable = "\tjQuery('form').eq(0).data('already_changed', true);"; // temporarily disable form change handler
		$jsEditable .= "\tjQuery('form').eq(0).data('already_changed', false);"; // re-enable form change handler
	}

	// process combos
	$templateCode = str_replace('<%%COMBO(id_Estado_Conservacion)%%>', $combo_id_Estado_Conservacion->HTML, $templateCode);
	$templateCode = str_replace('<%%COMBOTEXT(id_Estado_Conservacion)%%>', $combo_id_Estado_Conservacion->MatchText, $templateCode);
	$templateCode = str_replace('<%%URLCOMBOTEXT(id_Estado_Conservacion)%%>', urlencode($combo_id_Estado_Conservacion->MatchText), $templateCode);
	$templateCode = str_replace('<%%COMBO(id_Estado_Prestable)%%>', $combo_id_Estado_Prestable->HTML, $templateCode);
	$templateCode = str_replace('<%%COMBOTEXT(id_Estado_Prestable)%%>', $combo_id_Estado_Prestable->MatchText, $templateCode);
	$templateCode = str_replace('<%%URLCOMBOTEXT(id_Estado_Prestable)%%>', urlencode($combo_id_Estado_Prestable->MatchText), $templateCode);
	$templateCode = str_replace(
		'<%%COMBO(Fecha_Regsitro)%%>', 
		($selected_id && !$arrPerm['edit'] && ($noSaveAsCopy || !$arrPerm['insert']) ? 
			'<div class="form-control-static">' . $combo_Fecha_Regsitro->GetHTML(true) . '</div>' : 
			$combo_Fecha_Regsitro->GetHTML()
		), $templateCode);
	$templateCode = str_replace('<%%COMBOTEXT(Fecha_Regsitro)%%>', $combo_Fecha_Regsitro->GetHTML(true), $templateCode);

	/* lookup fields array: 'lookup field name' => ['parent table name', 'lookup field caption'] */
	$lookup_fields = ['id_Estado_Conservacion' => ['Estado_Conservacion', 'Estado de Conservacion'], 'id_Estado_Prestable' => ['Estado_Prestable', 'Estado Prestable'], ];
	foreach($lookup_fields as $luf => $ptfc) {
		$pt_perm = getTablePermissions($ptfc[0]);

		// process foreign key links
		if($pt_perm['view'] || $pt_perm['edit']) {
			$templateCode = str_replace("<%%PLINK({$luf})%%>", '<button type="button" class="btn btn-default view_parent" id="' . $ptfc[0] . '_view_parent" title="' . html_attr($Translation['View'] . ' ' . $ptfc[1]) . '"><i class="glyphicon glyphicon-eye-open"></i></button>', $templateCode);
		}

		// if user has insert permission to parent table of a lookup field, put an add new button
		if($pt_perm['insert'] /* && !Request::val('Embedded')*/) {
			$templateCode = str_replace("<%%ADDNEW({$ptfc[0]})%%>", '<button type="button" class="btn btn-default add_new_parent" id="' . $ptfc[0] . '_add_new" title="' . html_attr($Translation['Add New'] . ' ' . $ptfc[1]) . '"><i class="glyphicon glyphicon-plus text-success"></i></button>', $templateCode);
		}
	}

	// process images
	$templateCode = str_replace('<%%UPLOADFILE(id)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(Codigo_de_Registro)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(Codigo_Barras)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(id_Estado_Conservacion)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(id_Estado_Prestable)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(Fecha_Regsitro)%%>', '', $templateCode);

	// process values
	if($selected_id) {
		if( $dvprint) $templateCode = str_replace('<%%VALUE(id)%%>', safe_html($urow['id']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(id)%%>', html_attr($row['id']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(id)%%>', urlencode($urow['id']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(Codigo_de_Registro)%%>', safe_html($urow['Codigo_de_Registro']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(Codigo_de_Registro)%%>', html_attr($row['Codigo_de_Registro']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Codigo_de_Registro)%%>', urlencode($urow['Codigo_de_Registro']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(Codigo_Barras)%%>', safe_html($urow['Codigo_Barras']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(Codigo_Barras)%%>', html_attr($row['Codigo_Barras']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Codigo_Barras)%%>', urlencode($urow['Codigo_Barras']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(id_Estado_Conservacion)%%>', safe_html($urow['id_Estado_Conservacion']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(id_Estado_Conservacion)%%>', html_attr($row['id_Estado_Conservacion']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(id_Estado_Conservacion)%%>', urlencode($urow['id_Estado_Conservacion']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(id_Estado_Prestable)%%>', safe_html($urow['id_Estado_Prestable']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(id_Estado_Prestable)%%>', html_attr($row['id_Estado_Prestable']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(id_Estado_Prestable)%%>', urlencode($urow['id_Estado_Prestable']), $templateCode);
		$templateCode = str_replace('<%%VALUE(Fecha_Regsitro)%%>', app_datetime($row['Fecha_Regsitro']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Fecha_Regsitro)%%>', urlencode(app_datetime($urow['Fecha_Regsitro'])), $templateCode);
	} else {
		$templateCode = str_replace('<%%VALUE(id)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(id)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(Codigo_de_Registro)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Codigo_de_Registro)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(Codigo_Barras)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Codigo_Barras)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(id_Estado_Conservacion)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(id_Estado_Conservacion)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(id_Estado_Prestable)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(id_Estado_Prestable)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(Fecha_Regsitro)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(Fecha_Regsitro)%%>', urlencode(''), $templateCode);
	}

	// process translations
	$templateCode = parseTemplate($templateCode);

	// clear scrap
	$templateCode = str_replace('<%%', '<!-- ', $templateCode);
	$templateCode = str_replace('%%>', ' -->', $templateCode);

	// hide links to inaccessible tables
	if(Request::val('dvprint_x') == '') {
		$templateCode .= "\n\n<script>\$j(function() {\n";
		$arrTables = getTableList();
		foreach($arrTables as $name => $caption) {
			$templateCode .= "\t\$j('#{$name}_link').removeClass('hidden');\n";
			$templateCode .= "\t\$j('#xs_{$name}_link').removeClass('hidden');\n";
		}

		$templateCode .= $jsReadOnly;
		$templateCode .= $jsEditable;

		if(!$selected_id) {
		}

		$templateCode.="\n});</script>\n";
	}

	// ajaxed auto-fill fields
	$templateCode .= '<script>';
	$templateCode .= '$j(function() {';


	$templateCode.="});";
	$templateCode.="</script>";
	$templateCode .= $lookups;

	// handle enforced parent values for read-only lookup fields
	$filterField = Request::val('FilterField');
	$filterOperator = Request::val('FilterOperator');
	$filterValue = Request::val('FilterValue');

	// don't include blank images in lightbox gallery
	$templateCode = preg_replace('/blank.gif" data-lightbox=".*?"/', 'blank.gif"', $templateCode);

	// don't display empty email links
	$templateCode=preg_replace('/<a .*?href="mailto:".*?<\/a>/', '', $templateCode);

	/* default field values */
	$rdata = $jdata = get_defaults('Registros');
	if($selected_id) {
		$jdata = get_joined_record('Registros', $selected_id);
		if($jdata === false) $jdata = get_defaults('Registros');
		$rdata = $row;
	}
	$templateCode .= loadView('Registros-ajax-cache', ['rdata' => $rdata, 'jdata' => $jdata]);

	// hook: Registros_dv
	if(function_exists('Registros_dv')) {
		$args = [];
		Registros_dv(($selected_id ? $selected_id : FALSE), getMemberInfo(), $templateCode, $args);
	}

	return $templateCode;
}